    past_kv<span class="token punctuation">,</span> <span class="token comment"># bsz ∗ num_head ∗ qk_dim ∗ v_dim</span>
    decay_mask<span class="token punctuation">,</span> <span class="token comment"># num_head ∗ chunk_size ∗ chunk_size</span>
    chunk_decay<span class="token punctuation">,</span> <span class="token comment"># num_head ∗ 1 ∗ 1</span>
    inner_decay<span class="token punctuation">,</span> <span class="token comment"># num_head ∗ chunk_size</span>
<span class="token punctuation">)</span><span class="token punctuation">:</span>
    retention <span class="token operator">=</span> q @ k<span class="token punctuation">.</span>transpose<span class="token punctuation">(</span>−<span class="token number">1</span><span class="token punctuation">,</span> −<span class="token number">2</span><span class="token punctuation">)</span>
    retention <span class="token operator">=</span> retention ∗ decay_mask
